<?php	namespace Trade\Controller;	use Think\Controller;	class GlobalController extends Controller 	{		function _initialize()		{						$this->roleId = intval(Session('vipId'));			$this->username = Session('vipName');			if((empty($this->roleId) ||empty($this->username)) &&  strtolower(MODULE_NAME)!="login"){				redirect( U('login/login'));			}			//import("ORG.Util.frontPage");			///////////////////////////////  產品瀏覽紀錄 start  ////////////////////////////////////			//dump(date("m"));dump(date("d"));			// 取得使用者的 ip			if(!empty($_SERVER['HTTP_CLIENT_IP'])){				$guestIp = $_SERVER['HTTP_CLIENT_IP'];				}else if(!empty($_SERVER['HTTP_X_FORWARDED_FOR'])){				$guestIp = $_SERVER['HTTP_X_FORWARDED_FOR'];				}else{				$guestIp = $_SERVER['REMOTE_ADDR'];			}			//dump($guestIp);exit;			// 取得使用者的觀看紀錄			$isLooked = $_COOKIE[str_replace(".", "_", $guestIp).'isCome'];			//dump($isLooked);exit;								} 			}?>	